import React,{ useState, useEffect} from 'react';
import axios from 'axios';

function AccountScreen(){

	const [accScreen, setAccScreen] = useState(false);
	const [screen, setScreen] = useState('');
	const [data, setData] = useState({});

	const [username, setUsername] = useState('');
	const [settingUsername, setSettingUsername] = useState(false);

	useEffect(()=>{
		axios.post('http://127.0.0.1:8000/account/',{'username':localStorage.getItem('user223')})
		.then(res=>{
			setData(res.data.profile);
			setAccScreen(true);

		})
	},[]);


	const updateUsername=()=>{
		if (username==='') alert('Cannot asign empty value.');
	}

	return(
		<div className='AccountScreen'>
			{accScreen?<div>
				<h1 className='m-20'>Account</h1>
				<img className='profileImg' src={data.Image.Image} alt='Profile'/>
				<h4 className='m-20 f-cursive'>@{data.User.username}</h4>

				{settingUsername?<h3>First Name : <input type='text' 
					onChange={e=>{setUsername(e.target.value)}}/><button
									onClick={()=>{updateUsername(); setSettingUsername(false);}}
								>Set</button>
				</h3>:<h3>
					First Name : {data.User.first_name}<button
					onClick={()=>{setSettingUsername(true)}}
				>Update</button></h3>}



				<h3>Last Name : {data.User.last_name}</h3>
				<h3>Email : {data.User.email}</h3>
				{data.Service?<div>
					<h1 className='m-20'>Your services</h1>

				</div>:''}
			</div>:''}
		</div>
	);
}

export default AccountScreen;
