import React,{Component} from 'react';
import Navbar from '../Components/Navbar';
import SubMainScreen from './SubMainScreen';
import SearchScreen from './SearchScreen';

class MainScreen extends Component{
	state={
		'login':true,
		'screen':<SubMainScreen login={true} />,
		'mainPage':true,
	}


	handleMessages(){
		alert('messages');
	}

	handleLogin(){
		alert('Login');
	}

	handleSignup(){
		alert('Signup');
	}

	handleAccount(){
		alert('Account');
	}

	handleAboutUs(){
		alert('Comming Soon!');
	}

	handleLogout(){
		alert('You are loging out!');
	}

	handleSearch(){
		var input = prompt('Search here:');
		if(input !== null && input !== ''){
			this.setState({'screen':<SearchScreen Name={input}/>});
		}
	}

	handleGoToMain(){
		this.setState({'screen':<SubMainScreen login={true} />});
	}


	render(){
		return(
			<div className='MainScreen'>
				<Navbar 
				 handleMessages={this.handleMessages}
				 handleLogin={this.handleLogin}
				 handleSignup={this.handleSignup}
				 handleAccount={this.handleAccount}
				 handleLogout={this.handleLogout}
				 handleAboutUs={this.handleAboutUs}
				 login={this.state.login}
				/>
				<div className='breakpoint'></div>

				<button onClick={this.handleSearch.bind(this)} className='search-btn'>Search</button>
				{this.state.mainPage?'':<button onClick={this.handleGoToMain.bind(this)}>Back</button>}

				{this.state.screen}


			</div>
		);
	}
}

export default MainScreen;
