import React,{ useEffect ,useState} from 'react';
import MessageCard from '../Components/MessageCard';
import MessageBox from './MessageBox';
import axios from 'axios';


function MessageScreen(){

	const [data, setData] = useState([]);
	const [message, setMessage] = useState([]);
	const [msgBox, setMsgBox] = useState(false);


	useEffect(()=>{
		axios.post('http://127.0.0.1:8000/messageBox/',{'Username':localStorage.getItem('Username')}).then(res=>{
			setData(res.data);
		}).catch(err=>{
			console.error(err);
		})
	},[]);

	const handleClick = msgMan=>{
		axios.post('http://127.0.0.1:8000/messageBox/',{
		'Username':localStorage.getItem('Username'),
		'MessagePartner':msgMan
		}).then(res=>{
			setMessage(res.data);
			setMsgBox(true);
		}).catch(err=>{
			console.error(err);
		})
	}

	return(
		<div className='MessageScreen'>
			{msgBox?<div className='MessageBoxOuter'><h1 
			className='cross'
			onClick={()=>setMsgBox(false)}>X</h1><MessageBox data={data}/></div>:''}
			{data.map(d=><div key={d.id} onClick={()=>{handleClick(d.MessagePartner)}}><MessageCard 
			Username={d.Username}
			Name={d.MessagePartner}
			Unread = {d.UnreadMessages}
			/></div>)}

		</div>
	);
}

export default MessageScreen;
