import React,{ useState } from 'react';
import axios from 'axios';


function SignupScreen(props){

	const [username, setUsername]     = useState('');
	const [firstname, setFirstname]   = useState('');
	const [lastname, setLastname]     = useState('');
	const [email, setEmail]           = useState('');
	const [password1, setPassword1]   = useState('');
	const [password2, setPassword2]   = useState('');
	const [moNo, setMoNo]   		  = useState('');
	const [addrs, setAddrs]   		  = useState('');

	const [hidePass1, setHidePass1]   = useState(true);
	const [hidePass2, setHidePass2]   = useState(true);

	const [servicePF, setServicePF]   = useState(false);

	const handleSignup=(as)=>{
		if(password1 !== password2){
			alert('Passwords are not matching.');
		}else if(username==='' || password1==='' || email===''){
			alert('* fields are required.');
		}else{
			if(as==='user'){
				axios.post('http://127.0.0.1:8000/signup/',{
					'username':username,
					'first_name':firstname,
					'last_name':lastname,
					'email':email,
					'password':password1
				}).then(res=>{
					if(res.data.error){
						alert(res.data.error)
					}else{
						alert('Account is successfully created for '+username);
						localStorage.setItem('user223',username);
						props.afterSignup();
					}
				}).catch(err=>{
					alert('Please provide valid details.');
				})
			}else{
				axios.post('http://127.0.0.1:8000/signupAsProvider/',{
					'username':username,
					'first_name':firstname,
					'last_name':lastname,
					'email':email,
					'password':password1,
					'Address':addrs,
					'MobileNo':moNo
				}).then(res=>{
					if(res.data.error){
						alert(res.data.error)
					}else{
						alert('Survice Provider account is successfully created for '+username);
						localStorage.setItem('user223',username);
						props.afterSignup();
					}
				}).catch(err=>{
					alert('Please provide valid details.');
				})
			}
		}
	}

	return(
		<div className='SignupScreen'>
			<input type='text' value={username} 
				onChange={e=>{setUsername(e.target.value)}}
				placeholder='Username*' required
			/>
			<input type='text' value={firstname} 
				onChange={e=>{setFirstname(e.target.value)}}
				placeholder='Firstname'
			/>
			<input type='text' value={lastname} 
				onChange={e=>{setLastname(e.target.value)}}
				placeholder='Lastname'
			/>
			<input type='email' value={email} 
				onChange={e=>{setEmail(e.target.value)}}
				placeholder='Email*' required
			/>

			<input type={hidePass1?'password':'text'} 
				value={password1} onChange={e=>{setPassword1(e.target.value)}}
				placeholder='Password*' required
			/>
			<button onClick={()=>{setHidePass1(!hidePass1)}}>{hidePass1?'show':'hide'}</button>

			<input type={hidePass2?'password':'text'} 
				value={password2} onChange={e=>{setPassword2(e.target.value)}}
				placeholder='Confirm Password*' required
			/>
			<button onClick={()=>{setHidePass2(!hidePass2)}}>{hidePass2?'show':'hide'}</button>


			{servicePF?<input type='tel'
				value={moNo} onChange={e=>{setMoNo(e.target.value)}}
				placeholder='Mobile no with country code*' required
				pattern='[0-9]{2} [0-9]{10}'
			/>:''}

			{servicePF?<textarea rows='7'
				value={addrs} onChange={e=>{setAddrs(e.target.value)}}
				placeholder='Enter your full address.*' required
			></textarea>:''}

			<div>
				{servicePF?'':<h5>Signup as</h5>}


				{servicePF?<button 
					className='signup-btn'
					onClick={()=>setServicePF(false)}>back</button>:<button 
					className='signup-btn' 
					onClick={()=>handleSignup('user')}
				>User</button>}

				{servicePF?<button className='signup-btn' 
					onClick={()=>{handleSignup('service-provider')}}
				>Now Signup as Service Provider</button>:<button className='signup-btn' 
					onClick={()=>{setServicePF(true)}}
				>Service Provider</button>}
			</div>
		</div>
	);
}

export default SignupScreen;
