import React,{Component} from 'react';
import Navbar from '../Components/Navbar';
import SubMainScreen from './SubMainScreen';
import SearchScreen from './SearchScreen';
import MessageScreen from './MessageScreen';
import SignupScreen from './SignupScreen';

class MainScreen extends Component{
	state={
		'login':false,
		'screen':'',
		'mainPage':true,
	}

	componentDidMount(){
		this.setState({'screen':<SubMainScreen login={this.state.login} />});
	}

	handleMessages(){
		this.setState({'screen':<SignupScreen/>,'mainPage':false})
	}

	handleLogin(){
		alert('Login');
	}

	handleSignup(){
		this.setState({'screen':<MessageScreen/>,'mainPage':false})
	}

	handleAccount(){
		alert('Account');
	}

	handleAboutUs(){
		alert('Comming Soon!');
	}

	handleLogout(){
		alert('You are loging out!');
	}

	handleSearch(){
		var input = prompt('Search here:');
		if(input !== null && input !== ''){
			this.setState({'screen':<SearchScreen handleOpenService={this.handleOpenService} Name={input}/>,'mainPage':false});
		}
	}

	handleGoToMain(){
		this.setState({'screen':<SubMainScreen login={true} />,'mainPage':true});
	}

	handleOpenService(id){
		alert(id);
	}

	render(){
		return(
			<div className='MainScreen'>
				{localStorage.setItem('Username','sumit')}
				<Navbar 
				 handleMessages={this.handleMessages.bind(this)}
				 handleLogin={this.handleLogin}
				 handleSignup={this.handleSignup.bind(this)}
				 handleAccount={this.handleAccount}
				 handleLogout={this.handleLogout}
				 handleAboutUs={this.handleAboutUs}
				 login={this.state.login}
				/>
				<div className='breakpoint'></div>

				{this.state.mainPage?'':<button 
				onClick={this.handleGoToMain.bind(this)}
				>Back</button>}

				<button 
				onClick={this.handleSearch.bind(this)} 
				className='search-btn'>Search</button>

				{this.state.screen}


			</div>
		);
	}
}

export default MainScreen;
