import React,{ useState } from 'react';
import axios from 'axios';


function SignupScreen(){

	const [username, setUsername]     = useState('');
	const [firstname, setFirstname]   = useState('');
	const [lastname, setLastname]     = useState('');
	const [email, setEmail]           = useState('');
	const [password1, setPassword1]   = useState('');
	const [password2, setPassword2]   = useState('');

	const [hidePass1, setHidePass1]   = useState(true);
	const [hidePass2, setHidePass2]   = useState(true);

	const [servicePF, setServicePF]   = useState(false);

	const handleSignup=(as)=>{
		if(password1 !== password2){
			alert('Passwords are not matching.');
		}else if(username==='' || password1==='' || email===''){
			alert('* fields are required.');
		}else{
			axios.post('http://127.0.0.1:8000/signup/',{
				'username':username,
				'first_name':firstname,
				'last_name':lastname,
				'email':email,
				'password':password1
			}).then(res=>{
				if(res.data.error){
					alert(res.data.error)
				}else{
					if(as==='user'){
						alert('Account is successfully created for '+username)
					}
				}
			}).catch(err=>{
			console.error(err);
			})
		}
	}

	return(
		<div className='SignupScreen'>
			<input type='text' value={username} 
			onChange={e=>{setUsername(e.target.value)}}
			placeholder='Username*'
			/>
			<input type='text' value={firstname} 
			onChange={e=>{setFirstname(e.target.value)}}
			placeholder='Firstname'
			/>
			<input type='text' value={lastname} 
			onChange={e=>{setLastname(e.target.value)}}
			placeholder='Lastname'
			/>
			<input type='email' value={email} 
			onChange={e=>{setEmail(e.target.value)}}
			placeholder='Email*'
			/>

			<input type={hidePass1?'password':'text'} 
			value={password1} onChange={e=>{setPassword1(e.target.value)}}
			placeholder='Password*'
			/>
			<button onClick={()=>{setHidePass1(!hidePass1)}}>{hidePass1?'show':'hide'}</button>

			<input type={hidePass2?'password':'text'} 
			value={password2} onChange={e=>{setPassword2(e.target.value)}}
			placeholder='Confirm Password*'
			/>
			<button onClick={()=>{setHidePass2(!hidePass2)}}>{hidePass2?'show':'hide'}</button>


			<div>
				{servicePF?'':<h5>Signup as</h5>}


				{servicePF?<button 
				className='signup-btn'
				onClick={()=>setServicePF(false)}>back</button>:<button 
				className='signup-btn' 
				onClick={()=>handleSignup('user')}
				>User</button>}

				{servicePF?<button className='signup-btn' 
				onClick={()=>{handleSignup('user')}}
				>Now Signup as Service Provider</button>:<button className='signup-btn' 
				onClick={()=>{setServicePF(true)}}
				>Service Provider</button>}
			</div>
		</div>
	);
}

export default SignupScreen;
