import React,{ useState, useEffect } from 'react';
import axios from 'axios';


function Footer(props){
	const [FAQ, setFAQ] = useState(false);
	const [TC, setTC] = useState(false);
	const [PP, setPP] = useState(false);
	const [FAQData, setFAQData] = useState([]);

	useEffect(()=>{
		const url = localStorage.getItem('url');
		axios.get('FAQData/').then(res=>{
			setFAQData(res.data);
		});
	},[])

	return(
		<div className='Footer'>
			<div>
				<button onClick={()=>{
					setFAQ(!FAQ);
					setPP(false);
					setTC(false);
					}}>FAQ</button>
				<button onClick={()=>{
					setTC(!TC);
					setPP(false);
					setFAQ(false);
				}}>T&C</button>
				<button onClick={()=>{
					setPP(!PP);
					setFAQ(false);
					setTC(false);
				}}>Privacy Policy</button>
			</div>
			{FAQ?<div>
				<h1>FAQ</h1>

				<p>Q:How do you make money?</p>
				<p>A:In the begining everything is free for 
				everyone. But later we can charge on transactions.</p>
				<br/>
				<p>Q:Is it safe?</p>
				<p>It is 100% safe. You can read our Privacy Policy.</p>
				<br/>
			</div>:''}
			{TC?<div>
				<h1>Terms and Conditions</h1>
				<p>rules goes here!</p>
			</div>:''}
			{PP?<div>
				<h1>Privacy Policy</h1>
				<p>Here your data is 100% safe but we are not responsible 
				from outside of website. We are adding in policy.</p>
			</div>:''}
			<p>Â©saveyourtime2021</p>
		</div>
	);
}

export default Footer;
